mapper.select:
select i.*, m.name makerName, b.brand_name
from auto_info i,
auto_maker m,
auto_brand b
where i.maker_id = m.id
and i.brand_id = b.id and i.deleted = 0

    and i.auto_num like concat('%', #{autoInfo.autoNum}, '%')


    and i.maker_id = #{autoInfo.makerId}


    and i.brand_id = #{autoInfo.brandId}


    and i.info_type = #{autoInfo.infoType}


    and i.status = #{autoInfo.status}


    and i.registration_date >= #{autoInfo.lowRegistrationDate}


    and i.registration_date <= #{autoInfo.highRegistrationDate}


    and i.rent>= #{autoInfo.lowRent}


    and i.rent <= #{autoInfo.highRent}
